                  res.status(409).json({
                    Status: '409',
                    Error: error.message
                  });
                });
              }
            }
          }
        })["catch"](function (err) {
          res.status(409).json({
            Status: '409',
            Error: err
          });
        });
      } else {
        res.status(401).json({
          Status: '401',
          Error: 'User must be Verified To make this request'
        });
      }
    } else {
      res.status(409).json({
        Status: '409',
        Error: 'Failed to process request, Try again later'
      });
    }
  })["catch"](function (error) {
    res.status(500).json({
