                    bcrypt.hash(req.body.NewPassword, 10, (errr, hash) => {
                        if (err)
                        {
                            res.status(500).json({
                                Status: 500,
                                Error: err.message
                            });
                        }

                        else
                        {
                            pool.query(`UPDATE users set password='${hash}' where email='${email}'`, (errorFound, feedBack) => {
                                if (errorFound)
                                {
                                    res.status(500).json({
                                        Status: 500,
