                                            });
                                        }

                                        else
                                        {
                                            res.status(200);
                                        }
                                    });
                                }
                            });
                        }
                    });
                }
            });
            }
        });
    }

    if (req.body.Address)
    {
        pool.query(`UPDATE users set address='${req.body.Address}' where email='${email}'`, (error, success) => {
            if (error)
            {
                res.status(500).json({
                    Status: 500,
                    Error: error.message
                });   
            }

            else
            {
                pool.query(`select * from users where email='${email}'`, (error2, success2) => {
                    if (error)
                    {
                        res.status(500).json({
                            Status: 500,
                            Error: error.message
                        });
                    }

                    else
                    {
                        res.status(200);
                    }
                });
            }
        });
    }

    if (req.file.path)
    {
        pool.query(`UPDATE users set image='${req.file.path}' where email='${email}'`)
        .then((feedBack) => {
            pool.query(`select * from users where email='${email}'`, (err, success) => {
                if (err)
                {
                    res.status(500).json({
                        Status: 500,
                        Error: err.message
                    });
                }

                else
                {
                    res.status(200).json({
                        Status: 200,
                        Data: success.rows,
                        Success: 'Successfully Updated Profile'
                    });
                }
            });
        })

        .catch((error) => {
            res.status(500).json({
                Status: 500,
                Error: error.message
            });
        });
    }
};

